    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        double patrimonio;
        int estrato;
        
        patrimonio = Double.parseDouble(txtPatrimonio.getText());
        estrato = Integer.parseInt(txtEstrato.getText());
        
        double pagoMatricula = 50000; // Valor constante de matrÃ­cula

        if (patrimonio > 2000000 && estrato > 3) {
            double incremento = 0.03 * patrimonio;
            pagoMatricula += incremento;
        }
        
        txtPagoMatricula.setText(String.valueOf(pagoMatricula));
    }//GEN-LAST:event_btnCalcularActionPerformed

